$phpkd_usermo_files = array(
	DIR . '/usermo.php',
	DIR . '/includes/phpkd/usermo_plugins.php',
	DIR . '/includes/phpkd/usermo_functions.php',
	DIR . '/includes/xml/cpnav_phpkd_usermo.xml',
	DIR . '/includes/xml/bitfield_phpkd_usermo.xml',
	DIR . '/includes/xml/hooks_phpkd_usermo.xml',
	DIR . '/includes/functions_usermo_log.php',
	DIR . '/includes/md5_sums_phpkd_usermo.php',
	DIR . '/' . $vbulletin->config['Misc']['admincpdir'] . '/usermolog.php'
);

function phpkd_usermo_files($files)
{
	foreach ($files AS $fileid => $file)
	{
		if (file_exists($file))
		{
			unset($files[$fileid]);
		}
	}

	return $files;
}


$phpkd_usermo_files_not_exists = phpkd_usermo_files($phpkd_usermo_files);

if (is_array($phpkd_usermo_files_not_exists) AND !empty($phpkd_usermo_files_not_exists))
{
	$phpkd_usermo_absent_files .= "<ol>\n";
	foreach ($phpkd_usermo_files_not_exists AS $phpkd_usermo_absent_file)
	{
		$phpkd_usermo_absent_files .= "\t<li>" . $phpkd_usermo_absent_file . "</li>\n";
	}
	$phpkd_usermo_absent_files .= "</ol>\n";
	print_dots_stop();
	print_cp_message('Please make sure that you have uploaded the following files that came with the product "Username Change Manager" before installing or upgrading!' . $phpkd_usermo_absent_files);
}

$db->hide_errors();
// look to see if we already have old versions of this product installed
$oldpid = "usermo";
if ($existingprod = $db->query_first("
	SELECT *
	FROM " . TABLE_PREFIX . "product
	WHERE productid = '" . $db->escape_string($oldpid) . "'"
))
{
	print_dots_start($vbphrase['phpkd_usermo_deleting_old_product'] . " (Title: " . $existingprod['title'] . ", ID: " . $existingprod['productid'] . ", Version: " . $existingprod['version'] . ").<br />" . $vbphrase['please_wait'], ':', 'phpkd_usermo_uninstall_old');
	delete_product($oldpid);
	print_dots_stop();
}

// Import debug data in appropriate field
$info['revision'] = trim(substr(substr($arr['revision'], 10), 0, -1));
$info['released'] = trim(substr(substr($arr['released'], 6), 0, -1));
if ($vbulletin->options['phpkd_custompaid37_data'])
{
	$holder = unserialize($vbulletin->options['phpkd_custompaid37_data']);
	$holder[$info['productid']] = $info;
	$data = $db->escape_string(serialize($holder));
	$db->query_write("
		UPDATE " . TABLE_PREFIX . "setting
		SET value = '$data'
		WHERE varname = 'phpkd_custompaid37_data'
	");

	print_dots_start("Upgrading: " . $info['title'] . "<br />Version: " . $info['version'] . ", Revision: " . $info['revision'] . ", Released: " . $info['released'] . ".<br />" . $vbphrase['please_wait'], ':', 'phpkd_usermo_install_info');
	print_dots_stop();
}
else
{
	$holder[$info['productid']] = $info;
	$data = $db->escape_string(serialize($holder));

	$db->query_write("
		REPLACE INTO " . TABLE_PREFIX . "setting
			(varname, grouptitle, value, defaultvalue, datatype, optioncode, displayorder, advanced, volatile, validationcode, blacklist, product)
		VALUES
			('phpkd_custompaid37_data', 'version', '$data', '', 'free', '', '1000', '0', '1', '', '0', 'phpkd_frame_work')
	");

	$db->query_write("
		REPLACE INTO " . TABLE_PREFIX . "phrase
			(languageid, fieldname, varname, text, product, username, dateline, version)
		VALUES
			('-1', 'vbsettings', 'setting_phpkd_custompaid37_data_title', 'PHPKD Custom Paid Products\' Data (3.7.x) [Sensitive]', 'phpkd_frame_work', 'PHPKD', '" . TIMENOW . "', '1.0.0'),
